    private async Task SearchScoresAsync()
    {
        busy = true;

        scores = await StudentScoreService.GetAllAsync(schoolId: AppUser.SchoolId, classId: selectedClass?.Id,
                subject: subject, examType: examType, term: term, session: session.Year);

        busy = false;
    }

