    private void CloseDialogs()
    {
        // Reset fields to their defaults
        addUpdateDialogIsOpen = false;
        deleteDialogIsOpen = false;
        signUpDialogIsOpen = false;
        resetPasswordDialogIsOpen = false;
        rolePanelExpanded = false;
