    #region variables

    private StudentScore[] scores;
    private Class[] availableClasses;
    private Class selectedClass;
    private ClassReport classReport;
    private string[] classSubjects;
    private Mode mode = Mode.None;

    //private Mode mode = Mode.None;
    private bool busy;
